<script setup>
import {ref, onMounted} from 'vue'

const emit = defineEmits(['openSettings'])

const isOnline = ref(navigator.onLine)

onMounted(() => {
  window.addEventListener('online', () => {
    isOnline.value = true
  })

  window.addEventListener('offline', () => {
    isOnline.value = false
  })
})

const openSettings = () => {
  emit('openSettings')
}
</script>

